mapper.select:
SELECT nf.file_id, nf.folder_id, sc.category_name, nf.file_name, nf.is_important, nf.created_at
FROM note_file nf
INNER JOIN script_category sc ON nf.category_id = sc.category_id
WHERE
    folder_id = #{folderId}
    AND nf.user_id = #{userId}

SELECT folder_id, parent_folder_id, folder_name, created_at
FROM note_folder
WHERE
    parent_folder_id = #{folderId}
    AND user_id = #{userId}
