mapper.update:
UPDATE script
SET is_recording = #{isRecording}
WHERE
    script_id = #{scriptId}
    AND user_id = #{userId}

UPDATE script
SET audio_file_id = #{audioFileId}
WHERE
    script_id = #{scriptId}
    AND user_id = #{userId}

mapper.select:
SELECT keyword_id, paragraph_id, user_id, keyword, created_at, updated_at
FROM script_paragraph_keyword
WHERE
    paragraph_id = #{paragraphId}
    AND user_id = #{userId}

mapper.update:
UPDATE script_paragraph

    start_time = #{startTime},
    end_time = #{endTime},
    paragraph_content = #{paragraphContent},
    memo_content = #{memoContent},
    is_bookmarked = #{isBookmarked}

WHERE
    paragraph_id = #{paragraphId}
    AND user_id = #{userId}

mapper.delete:
DELETE FROM script_paragraph
WHERE
    paragraph_id = #{paragraphId}
    AND user_id = #{userId}

DELETE FROM script
WHERE
    script_id = #{scriptId}
    AND user_id = #{userId}

DELETE FROM script_paragraph_keyword
WHERE
    script_id = #{scriptId}
    AND user_id = #{userId}

DELETE FROM script_paragraph
WHERE
    script_id = #{scriptId}
    AND user_id = #{userId}

DELETE FROM script_paragraph_keyword
WHERE
    paragraph_id = #{paragraphId}
    AND keyword = #{keyword}
    AND user_id = #{userId}
